import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-comandos',
  templateUrl: './comandos.component.html',
  styleUrls: ['./comandos.component.css']
})
export class ComandosComponent implements OnInit {

  constructor(private api: ApiService){}

  categorias: string[] = [];
  datosPorCategoria: { [key: string]: any } = {};

  ngOnInit(): void {
    this.api.getComandos().forEach(comando => {
      if (!this.categorias.includes(comando.categoria)) {
        this.categorias.push(comando.categoria);
        this.datosPorCategoria[comando.categoria] = [comando];
      } else {
        this.datosPorCategoria[comando.categoria].push(comando);
      }
    });
  }

  obtenerDatosPorCategoria(categoria: string): [] {
    return this.datosPorCategoria[categoria];
  }


}
