import { Component } from '@angular/core';

@Component({
  selector: 'app-carrousel-home',
  templateUrl: './carrousel-home.component.html',
  styleUrls: ['./carrousel-home.component.css']
})
export class CarrouselHomeComponent {

}
<div class="h-96 w-full p-24 text-black grid grid-cols-2">

    <div class="w-full mt-5 text-2xl p-4 pt-0 ml-24">
        <p id="linux-info">
            Linux es el nombre que reciben una serie de sistemas operativos de tipo Unix bajo la licencia GNU GPL (General Public License o Licencia Pública General de GNU) que son su mayoría gratuitos y con todo lo necesario para hacer funcionar un PC, con la peculiaridad de que podemos instalar un sistema muy ligero e ir añadiendo todo lo necesario posteriormente o según lo vayamos necesitando.
            <br><br>
        </p>
    </div>



    <!-- Contenedor de las imágenes -->
    <div class="contenedor-imagenes-opacity">
        <!-- Imágenes que cambiarán -->
        <img src="data:image/png;base64," alt="Imagen 1" class="imagen-cambiante-opacity aparecer">
        <img src="data:image/png;base64," alt="Imagen 2" class="imagen-cambiante-opacity">
        <img src="imagen3.jpg" alt="Imagen 3" class="imagen-cambiante-opacity">

        <!-- Agrega más imágenes según sea necesario -->
    </div>

   <script>
    function carrouselOpacity(){
    // JavaScript para hacer que las imágenes cambien automáticamente
    document.addEventListener('DOMContentLoaded', function () {
        var imagenes = document.querySelectorAll('.imagen-cambiante-opacity');
        var index = 0;

        setInterval(function () {
            // Hacer que la imagen actual desaparezca
            imagenes[index].classList.remove('aparecer');

            // Calcular el índice de la siguiente imagen
            index = (index + 1) % imagenes.length;

            // Hacer que la siguiente imagen aparezca
            imagenes[index].classList.add('aparecer');
        }, 5000); // Cambia el tiempo (5000ms = 5 segundos) según tus preferencias
    });
}
   </script>

</div>
