import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-ver-comando',
  templateUrl: './ver-comando.component.html',
  styleUrls: ['./ver-comando.component.css']
})
export class VerComandoComponent implements OnInit{

   constructor(private api: ApiService, private actRoute: ActivatedRoute){}

  nombreComando = ""
  descripcionComando = ""
  tagDescripcion = ""
  tagComando = ""

  ngOnInit(): void {
    this.actRoute.params.subscribe(params => {
      const nombre = params['nombre'];
      this.api.getComando(nombre).subscribe((comando) => {
      this.nombreComando = comando[0].nombre
      this.descripcionComando = comando[0].descripcion
      this.api.getTag(nombre).subscribe((tag) => {
        this.tagDescripcion = tag.descripcion
        this.tagComando = tag.tag
      })
    })
  });

  }

}
