import { Component } from '@angular/core';

@Component({
  selector: 'app-ver-comando',
  templateUrl: './ver-comando.component.html',
  styleUrls: ['./ver-comando.component.css']
})
export class VerComandoComponent {
  nombreComando = "shutdown"
}
<div id="contenido-comando" class="text-black w-5/6 float-right h-full">
    <div class="container p-4 text-center">
        <p class="text-4xl">{{ nombreComando }}</p>

        <div class="border p-2 w-1/2 text-justify h-16 mt-2 rounded">
            shutdown -c
        </div>

    </div>
</div>
