import { Component, OnInit } from '@angular/core';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-comandos',
  templateUrl: './comandos.component.html',
  styleUrls: ['./comandos.component.css']
})
export class ComandosComponent implements OnInit {

  constructor(private api: ApiService){}

  categoriaComandos = new Array()
  nombreComando = ''
  nombreCategoria = ''
  descripcionComando = ''
  data = []

  ngOnInit(): void {
    this.api.getComandos().subscribe((data) => {
      this.nombreCategoria = data[0].categoria
      this.nombreComando = data[0].nombre

      for(let i=0; i<data.length; i++)
      {
        this.categoriaComandos.push(data[0].categoria)
      }

      this.descripcionComando = data[0].descripcion
      this.data = data
      console.log(this.categoriaComandos)
    })
  }


}
