import { Component } from '@angular/core';

@Component({
  selector: 'app-ver-comando',
  templateUrl: './ver-comando.component.html',
  styleUrls: ['./ver-comando.component.css']
})
export class VerComandoComponent {
  nombreComando = "shutdown"
}
<div id="contenido-comando" class="text-black w-5/6 right-0 inset-y-0 mt-16 absolute overflow-auto">
    <div class="container p-4 text-center flex flex-col justify-center items-center">


        <div class="bg-black text-white p-2 w-1/2 text-justify h-12 mt-2 rounded">
            <p class="mt-1">user@machine: {{ nombreComando }}</p>
        </div>


    </div>
</div>
